INSERT INTO `t_student` VALUES ('STU201709291720560001','admin','a285aea063769c6638651d74fb212ead14bde78a6b236a3891d39c16311d5d1a1fe673fc954ae86dd7865318dd6468226fe1fa7c7567212eeb63e6740fccb797','18771099612','1335250574@qq.com','xxx小学','开封','3','2','张三','','370c3c9f6134e58074e6342f55562f61','2','2017-09-29 17:20:56'),('STU201709301049030001','zhu123','408bdabfbb504b82c7c01e769ce9d97257ae061d35064df42e8ed0f0ba4b7c4cca9d0623587ec598e94e97b114a4ddd7ec6f6aef513a6fff92ce2a21382ead2c','13845879172','397534596@qq.com','濮阳市范县第一初级中学','濮阳','1','1','朱浩强','','cfd36854b50f6d726c68d4ab24e72c7f','1','2017-09-30 10:49:03'),('STU201709301537110001','mingzhanghui','c9cf74c44c866c00b8500d4df4106ddf4b7e58a906a4e6905b6fc8da45df115e52265cc58e04ff15782f53fce09c733c867a4775d303f25f694da57a5a7e7f11','13001919562','1335250574@qq.com','开封市五中','开封','3','1','明章辉','420983199110285211','e0bbd08d2529c5e83fe5bc6a754215ca','2','2017-09-30 15:37:11'),('STU201710091032370001','gmy1234','ca5d7a4b3e526518b2d80921643733ab0c8541d1516a39b519e85f6e3344cd93173023c1ab2b278fbe6f740163b683a5d19b019de90b1f01306b154174d1088a','13691150835','gmy1234@163.com','濮阳市范县第一初级中学','濮阳','4','1','关梦莹','','ad78cb9c63780d91e31008b8a2677b93','1','2017-10-09 10:32:37'),('STU201710111556290002','ma18011231018','250c42433f3e9ada682861564b08a35732a549e24b05408f01988690668835c1939d9856e321a37ad3a4b5f77e64f5ad526db179e85d900f5ffb87df617f4fb6','18011231018','1@1.1','开封立洋外国语学校','开封','8','8','范群英','110102199909090023','d314144603e6aa08c9c30be6d82cd59c','1','2017-10-11 15:56:29'),('STU201710230912470001','花前月下在线','0c5d264b1bb092c2a9190dcd289bba2a2b4bd38c6efe6f8bb43cc79b2fb456144ad46e6a842768e59bb9d83b2ed30646b8c843b05e14d314caf217f6ada5bbc3','18618307756','','濮阳市范县第一初级中学','濮阳','1','1','赵凡','','53b94be88116fd77ed7ba0aa729e4bda','1','2017-10-23 09:12:47'),('STU201710231417090001','我是来看看的','6fc11b259fdb582537b9b948f242e482860d1d3b6683d0573e43ae72760b93bdb7220b3144a8241ffd1ee19745ac840a870b4e892997a790b3b28385b142dbe1','18210084150','','开封市