DROP TABLE IF EXISTS `t_video`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `t_video` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `type` enum('学校介绍','宋城教育','龙都教育','课程','新闻','校长寄语','其他') NOT NULL DEFAULT '学校介绍' COMMENT '视频类型 type: enum(''学校介绍'',''宋城教育'',''龙都教育'')',
  `name` varchar(100) NOT NULL DEFAULT '0' COMMENT '视频标题',
  `location` varchar(255) DEFAULT NULL COMMENT '视频url',
  `poster` varchar(255) DEFAULT NULL COMMENT '视频海报url',
  `content` text COMMENT '视频简介',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=40 DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

